.header {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background-color: rgba(1, 1, 1, 0.5); 
    padding: 10px 10%;
    display: flex;
    align-items: center;
    justify-content: space-between;
    z-index: 1000;
}

.logo {
    font-size: 25px;
    text-transform: uppercase;
    color: #ffffff;
    font-weight: 700;
}

.navigation ul {
    list-style: none;
}

.header .navigation ul li {
    float: left;
    position: relative;
}

.header .navigation ul li a {
    font-size: 18px;
    /* color: #ffffff; */
    color: #ffffff !important;
    text-decoration: none;
    padding: 20px;
    display: block;
    transition: all .2s ease;
}

.header .navigation ul li a:hover {
    background-color: rgba(0, 187, 255, 0.7);
    }

.header .navigation ul li ul {
    position: absolute;
    right: 0;
    width: 300px;
    background-color: rgba(1, 1, 1, 0.5);
    display: none;
}

.header .navigation ul li ul li a {
    font-size: 15px;
    text-transform: capitalize;
}

.header .navigation ul li ul li ul {
    position: absolute;
    top: 0;
    right: 300px;
}

.header .navigation ul li ul li {
    width: 100%;
}

.header .navigation ul li:hover>ul {
    display: initial;
}

#toggle,
.header label {
    display: none;
    cursor: pointer;
}

.menu {
    width: 45px;
    height: 35px;
}

@media (max-width: 950px) {
    .header {
        padding: 10px 10%;
        /* Asegura que el padding sea consistente en dispositivos m贸viles */
    }

    .header label {
        display: initial;
    }

    .header .navigation {
        position: absolute;
        top: 100%;
        left: 0;
        right: 0;
        background-color: rgba(1, 1, 1, 0.7);
        display: none;
    }

    .header .navigation ul {
        padding: 0;
    }

    .header .navigation ul li {
        width: 100%;
        float: none;
        /* Asegura que los elementos se apilen verticalmente en m贸viles */
    }

    .header .navigation ul li a {
        padding: 8px 30px 8px 10%;
    }

    .header .navigation ul li ul {
        position: relative;
        width: 100%;
        left: 0;
    }

    .header .navigation ul li ul {
        background-color: rgba(1, 1, 1, 0.7);
    }

    .header .navigation ul li ul li ul {
        position: relative;
        width: 100%;
        left: 0;
    }

    #toggle:checked~.navigation {
        display: block;
    }
}

/*Esto es del apartado de abajo, vendria a ser el cuerpo o contenido de inicio*/
.home {
    height: 100vh;
    display: flex;
    align-items: center;
    padding: 0 4%;

}
/*Aqui se cambia el color del fondo de h1, h2, y h3 */
.home-content {
    /*background: yellowgreen;*/
    max-width: 600px;
}

.home-content h1 {
    font-size: 56px;
    font-weight: 700;
    line-height: 1.2;
}

.home-content h3 {
    font-size: 32px;
    font-weight: 700;
    color: #0dbfff;
}

.home-content p {
    font-size: 16px;
    margin: 20px 0 40px;

}

/* Botones */
.home-content .btn-box {
    display: flex;
    justify-content: space-between;

    width: 345px;
    height: 50px;
    /*background: yellowgreen;*/
}
/*color y borde de botones */
.btn-box a {
    position: relative;
    display: inline-flex;
    justify-content: center;
    align-items: center;A
    width: 150px;
    height: 100%;
    background: #0dbfff;
    border: 2px solid #0dbfff;
    border-radius: 8px;
    font-size: 19px;
    color: #081b29;
    text-decoration: none;
    font-weight: 600;
    letter-spacing: 1px;
    z-index: 1;
    overflow: hidden;
    transition: .5s;

}
/* Color de boton izquiero */
.btn-box a:hover {
    color: #0dbfff;

}

.btn-box a:nth-child(2) {
    background: transparent;
    color: #0dbfff;
}
/*color del boton derecho*/
.btn-box a:nth-child(2):hover {
    color: #081b29;

}
.btn-box a:nth-child(2)::before {
background: #0dbfff;

}

.btn-box a::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 0;
    height: 100%;
    background: #081b29;
    z-index: -1;
    transition: .5s;
}

.btn-box a:hover::before {
    width: 100%;

}
